import React from "react";
import style from "../assets/Styles/Body.module.scss";

import { dateString } from "../utils/getDate";

export default function Body({ messages, lastMessageRef }) {
  return (
    <>
      <div>
        <div className={style.chatBox}>
          <div className={style.timeDiv}>
            <p>{dateString}</p>
          </div>
          {messages.map((message) =>
            message.name === localStorage.getItem("userName") ? (
              <div key={message.id}>
                <div className={`${style.messageDiv} ${style.youSend} `}>
                  <p>{message.text}</p>
                </div>
              </div>
            ) : (
              <div key={message.id}>
                <div className={`${style.messageDiv} ${style.youReceive} `}>
                  <p>{message.text}</p>
                </div>
              </div>
            )
          )}
          <div ref={lastMessageRef} />
        </div>
      </div>
    </>
  );
}
