import MessageStyle from "../assets/Styles/Message.module.scss";
import React, { useState, useEffect } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:3000"); // Replace with your server URL

export default function Message() {
  // State for storing messages and current message
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState("");
  // Function to send a message
  const sendMessage = () => {
    if (currentMessage) {
      socket.emit("message", currentMessage);
      setCurrentMessage("");
    }
  };
  useEffect(() => {
    // Listen for incoming messages
    socket.on("message", (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });
  }, []);

  const isSending = true;

  var messageBoxStyle;
  if (isSending) {
    messageBoxStyle = MessageStyle.youSend;
  } else {
    messageBoxStyle = MessageStyle.youReceive;
  }
  return (
    <>
      <div className={`${MessageStyle.messageDiv} ${messageBoxStyle}`}>
        <h2 className={`${MessageStyle.youSend}`}>
          {messages.map((message, index) => (
            <div key={index}>{message}</div>
          ))}
        </h2>
      </div>
      {(messageBoxStyle = true)}
      <div className={`${MessageStyle.messageDiv} ${messageBoxStyle}`}>
        <h2 className={`${MessageStyle.youSend}`}>njsn?</h2>
      </div>
      <div>
        <input
          type="text"
          placeholder="Type a message..."
          value={currentMessage}
          onChange={(e) => setCurrentMessage(e.target.value)}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </>
  );
}
