import React from "react";
import Chatroom from "./Chatroom";
import TimeStamp from "./TimeStamp";
import style from "../assets/Styles/Body.module.scss";

const Body = ({ messages, lastMessageRef }) => {
  return (
    <>
      <Chatroom>
        <div>
          <TimeStamp />
          {messages.map((message) =>
            message.name === localStorage.getItem("userName") ? (
              <div key={message.id}>
                <div className={`${style.messageDiv} ${style.youSend} `}>
                  <p>{message.text}</p>
                </div>
              </div>
            ) : (
              <div key={message.id}>
                <div className={`${style.messageDiv} ${style.youReceive} `}>
                  <p>{message.text}</p>
                </div>
              </div>
            )
          )}

          <div ref={lastMessageRef} />
        </div>
      </Chatroom>
    </>
  );
};

export default Body;
