import { React, useState, useEffect, useRef } from "react";

import MessageBody from "./MessageBody";
import Header from "./Header";
import messageBoxStyles from "../assets/Styles/MessageBox.module.scss";
import Input from "./Input";
// import "../assets/Fonts/roboto.scss";
export default function MessageBox({ socket }) {
  const [messages, setMessages] = useState([]);
  const [typingStatus, setTypingStatus] = useState("");
  const lastMessageRef = useRef(null);
  useEffect(() => {
    socket.on("messageResponse", (data) => setMessages([...messages, data]));
  }, [socket, messages]);
  useEffect(() => {
    socket.on("typingResponse", (ndata) => setTypingStatus(ndata));
  }, [socket, messages]);

  useEffect(() => {
    // 👇️ scroll to bottom every time messages change
    lastMessageRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);
  useEffect(() => {
    socket.on("typingResponse", (data) => setTypingStatus(data));
  }, [socket]);
  return (
    <div className={messageBoxStyles.messageBoxDiv}>
      <div className="testDiv">
        <Header />

        <MessageBody
          messages={messages}
          typingThis={typingStatus}
          lastMessageRef={lastMessageRef}
        />
        <Input socket={socket} />
      </div>
    </div>
  );
}
