import React from "react";
import MessagesContainer from "./MessagesContainer";
import MessageStyle from "../assets/Styles/Message.module.scss";
import TimeStamp from "./TimeStamp";

const MessageBody = ({ messages, lastMessageRef, typingThis }) => {
  return (
    <>
      <MessagesContainer>
        <div>
          <TimeStamp />
          {messages.map((message) =>
            message.name === localStorage.getItem("userName") ? (
              <div key={message.id}>
                <div
                  className={`${MessageStyle.messageDiv} ${MessageStyle.youSend} `}
                >
                  <p>{message.text}</p>
                </div>
              </div>
            ) : (
              <div key={message.id}>
                <div
                  className={`${MessageStyle.messageDiv} ${MessageStyle.youReceive} `}
                >
                  <p className={MessageStyle.senderName}>{message.name}</p>
                  <p className={MessageStyle.senderText}>{message.text}</p>
                </div>
              </div>
            )
          )}

          <div className="message__status">
            <p>{typingThis}</p>
          </div>
          <div ref={lastMessageRef} />
        </div>
      </MessagesContainer>
    </>
  );
};

export default MessageBody;
