import MessageStyle from "../assets/Styles/Message.module.scss";
export default function Message() {
  const isSending = true;
  var messageBoxStyle;
  if (isSending) {
    messageBoxStyle = MessageStyle.youSend;
  } else {
    messageBoxStyle = MessageStyle.youReceive;
  }
  return (
    <>
      <div className={`${MessageStyle.messageDiv} ${messageBoxStyle}`}>
        <h2 className={`${MessageStyle.youSend}`}>`slm ${"\n"} njsn?`</h2>
      </div>
      {(messageBoxStyle = false)}
      <div className={`${MessageStyle.messageDiv} ${messageBoxStyle}`}>
        <h2 className={`${MessageStyle.youSend}`}>njsn?</h2>
      </div>
      {(messageBoxStyle = true)}
      <div className={`${MessageStyle.messageDiv} ${messageBoxStyle}`}>
        <h2 className={`${MessageStyle.youSend}`}>njsn?</h2>
      </div>
    </>
  );
}
