import React from "react";
import MessagesContainer from "./MessagesContainer";
import MessageStyle from "../assets/Styles/Message.module.scss";
import TimeStamp from "./TimeStamp";
const MessageBody = ({ messages, lastMessageRef, typingThis }) => {
  var messageBoxStyle;

  messages.map((message) =>
    message.name === localStorage.getItem("userName")
      ? (messageBoxStyle = MessageStyle.youSend)
      : (messageBoxStyle = MessageStyle.youReceive)
  );

  return (
    <>
      <MessagesContainer>
        {messages.map((message) => (
          <div className="message__container">
            <div className="message__chats" key={message.id}>
              <div className={`${MessageStyle.messageDiv} ${messageBoxStyle}`}>
                <p>{message.text}</p>
              </div>
            </div>
          </div>
        ))}
        <div className="message__status">
          <p>{typingThis}</p>
        </div>
        <div ref={lastMessageRef} />
      </MessagesContainer>
    </>
  );
};

export default MessageBody;
