import React from "react";
import { useNavigate } from "react-router-dom";
import MessagesBox from "./MessagesBox";
import MessageStyle from "../assets/Styles/Message.module.scss";
const MessageBody = ({ messages, lastMessageRef, typingThis }) => {
  const navigate = useNavigate();

  const handleLeaveChat = () => {
    localStorage.removeItem("userName");
    navigate("/");
    window.location.reload();
  };
  const isSending = true;

  var messageBoxStyle;
  if (isSending) {
    messageBoxStyle = MessageStyle.youSend;
  } else {
    messageBoxStyle = MessageStyle.youReceive;
  }
  return (
    <>
      <header className="chat__mainHeader">
        <p>Collage study groups</p>
        <button className="leaveChat__btn" onClick={handleLeaveChat}>
          LEAVE CHAT
        </button>
      </header>
      <MessagesBox>
        <div className="message__container">
          {messages.map((message) =>
            message.name === localStorage.getItem("userName") ? (
              <div className="message__chats" key={message.id}>
                <p className="sender__name">{localStorage.getItem("name")}</p>
                <div
                  className={`${MessageStyle.messageDiv} ${messageBoxStyle}`}
                >
                  <p className={`${MessageStyle.youSend}`}>{message.text}</p>
                </div>
              </div>
            ) : (
              <div className="message__chats" key={message.id}>
                <p>{message.name}</p>
                <div className="message__recipient">
                  <p>{message.text}</p>
                </div>
              </div>
            )
          )}

          <div className="message__status">
            <p>{typingThis}</p>
          </div>
          <div ref={lastMessageRef} />
        </div>
      </MessagesBox>
    </>
  );
};

export default MessageBody;
